import React from "react";
import "../CSS/History.css";
import { useState } from "react";
function History() {
    const [date, setDate] = useState("");
    const [symbol, setSymbol] = useState("");
    const [buy, setBuy] = useState("");
    const [sell, setSell] = useState("");
    const [shares, setShares] = useState("");
    const [time, setTime] = useState("");
    const [note, setNote] = useState("");

    const addItem = async () => {
        // const pl = (sell - buy)
        const raw = {
            id: Math.random(),
            date: date,
            symbol: symbol,
            buy: buy,
            sell: sell,
            shares: shares,
            time: time,
            note: note,
            pl: sell - buy,
        };

        const requestOptions = {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(raw),
        };

        await fetch(" http://localhost:8000/history", requestOptions);
    };
    return (
        <div className="HistoryPage">
            <input
                className="date-history"
                placeholder="Date"
                onChange={(e) => {
                    setDate(e.target.value);
                }}
            />
            <input
                className="symbol-history"
                placeholder="Ticker"
                onChange={(e) => {
                    setSymbol(e.target.value);
                }}
            />
            <input
                className="buy-history"
                placeholder="Buy Price"
                onChange={(e) => {
                    setBuy(e.target.value);
                }}
            />
            <input
                className="sell-history"
                placeholder="Sell Price"
                onChange={(e) => {
                    setSell(e.target.value);
                }}
            />
            <input
                className="shares-history"
                placeholder="Shares"
                onChange={(e) => {
                    setShares(e.target.value);
                }}
            />
            <input
                className="time-history"
                placeholder="Total Time"
                onChange={(e) => {
                    setTime(e.target.value);
                }}
            />
            <textarea
                className="note-history"
                placeholder="Enter Note...."
                onChange={(e) => {
                    setNote(e.target.value);
                }}
            />
            <button className="submit-history" onClick={addItem}>
                Submit
            </button>
        </div>
    );
}

export default History;
