import React from "react";
import "../CSS/Patterns.css";
import ascendingScallop from "../PICTURES/ascendingScallop.png";
import ascendingTriangle from "../PICTURES/ascendingTriangle.png";
function Patterns() {
    // const images = [ascendingScallop, ascendingTriangle];
    const items = [];
    function importAll(r) {
        let images = {};
        r.keys().map((item, index) => {
            images[item.replace("./", "")] = r(item);
        });
        return images;
    }
    for (const property in images) {
        items.push(property);
    }
    // console.log(images);
    const images = importAll(
        require.context("../PICTURES", false, /\.(png|jpe?g|svg)$/)
    );
    console.log(images);
    return (
        <div className="PatternsPage">
            {items.map((item, index) => {
                return <img src={item}></img>;
            })}
        </div>
    );
}

export default Patterns;
