import React from "react";
import "../CSS/Charts.css";
import { AdvancedChart, Screener, MiniChart } from "react-tradingview-embed";

function Charts() {
    const stocks = ["NYSE:F", "NYSE:GME"];
    return (
        <div className="ChartPage">
            <div className="Chart">
                <div className="chart1">
                    <AdvancedChart
                        widgetProps={{
                            autosize: true,
                            height: "100%",
                            width: "100%",
                            symbol: "NASDAQ:AAPL",
                            timezone: "Etc/UTC",
                            theme: "dark",
                            style: "1",
                            locale: "en",
                            toolbar_bg: "#f1f3f6",
                            enable_publishing: false,
                            withdateranges: true,
                            range: "1D",
                            hide_side_toolbar: false,
                            allow_symbol_change: true,
                            watchlist: ["NASDAQ:AAPL"],
                            // details: true,
                            hotlist: true,
                            calendar: true,
                            show_popup_button: true,
                            studies: ["RSI@tv-basicstudies"],
                        }}
                    />
                </div>
            </div>
            <div className="Screener">
                <div className="screen">
                    <Screener
                        widgetProps={{
                            // autosize: true,
                            // width: "100%",
                            // height: "100vh",
                            defaultColumn: "overview",
                            defaultScreen: "most_capitalized",
                            market: "america",
                            showToolbar: true,
                            colorTheme: "dark",
                            locale: "en",
                        }}
                    />
                </div>
            </div>
            <div className="MassCharts">
                {stocks.map((item, index) => {
                    return (
                        <MiniChart
                            key={index}
                            widgetProps={{
                                autosize: true,
                                height: "100%",
                                width: "100%",
                                symbol: item,
                                timezone: "Etc/UTC",
                                theme: "dark",
                                style: "1",
                                locale: "en",
                                range: "1D",
                            }}
                        />
                    );
                })}
            </div>
        </div>
    );
}

export default Charts;
