import React from "react";
import "../CSS/History.css";
import { useState } from "react";
function History() {
    const [date, setDate] = useState("");
    const [symbol, setSymbol] = useState("");
    const [buy, setBuy] = useState("");
    const [sell, setSell] = useState("");
    const [shares, setShares] = useState("");
    const [time, setTime] = useState("");
    const [note, setNote] = useState("");
    const [side, setSide] = useState("");
    const [history, setHistory] = useState({});
    console.log(side);
    const addItem = async () => {
        let roi = 0.0;
        if (side === "Long") {
            roi =
                (((parseFloat(sell) - parseFloat(buy)) * parseFloat(shares)) /
                    (parseFloat(buy) * parseFloat(shares))) *
                100;
        } else {
            roi =
                (((parseFloat(sell) - parseFloat(buy)) * parseFloat(shares)) /
                    (parseFloat(sell) * parseFloat(shares))) *
                100;
        }
        console.log(roi);
        let win = 1;
        if (parseFloat(sell) - parseFloat(buy) >= 0) {
            win = 1;
        } else {
            win = 0;
        }
        const raw = {
            id: Math.random(),
            date: date,
            symbol: symbol,
            buy: parseFloat(buy).toFixed(2),
            sell: parseFloat(sell).toFixed(2),
            shares: parseFloat(shares),
            time: time,
            note: note,
            pl: (
                (parseFloat(sell) - parseFloat(buy)) *
                parseFloat(shares)
            ).toFixed(2),
            side: side,
            roi: roi.toFixed(2).toString() + "%",
            win: win,
        };

        const requestOptions = {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(raw),
        };

        await fetch(" http://localhost:8000/history", requestOptions);

        setDate("");
        setSymbol("");
        setBuy("");
        setSell("");
        setShares("");
        setTime("");
        setNote("");

        fetch(" http://localhost:8000/history", {
            method: "GET",
            headers: { "Content-Type": "application/json" },
        })
            .then((response) => response.json())
            .then((result) => {
                setHistory(result);
                console.log(result);
            })
            .catch((error) => console.log("error", error));
    };

    return (
        <div className="HistoryPage">
            <div className="side-history">
                <div className="long-box">
                    <div>Long</div>
                    <input
                        type="checkbox"
                        onChange={(e) => {
                            if (e.target.checked) {
                                setSide("");
                                setSide("Long");
                            } else {
                                setSide("Short");
                            }
                        }}
                    ></input>
                </div>
                <div className="short-box">
                    <div>Short</div>
                    <input
                        type="checkbox"
                        onChange={(e) => {
                            if (e.target.checked) {
                                setSide("");
                                setSide("Short");
                            } else {
                                setSide("Long");
                            }
                        }}
                    ></input>
                </div>
            </div>
            <input
                className="date-history"
                placeholder="Date"
                onChange={(e) => {
                    setDate(e.target.value);
                }}
                value={date}
            />
            <input
                className="symbol-history"
                placeholder="Ticker"
                onChange={(e) => {
                    setSymbol(e.target.value);
                }}
                value={symbol}
            />
            <input
                className="buy-history"
                placeholder="Buy Price"
                onChange={(e) => {
                    setBuy(e.target.value);
                }}
                value={buy}
            />
            <input
                className="sell-history"
                placeholder="Sell Price"
                onChange={(e) => {
                    setSell(e.target.value);
                }}
                value={sell}
            />
            <input
                className="shares-history"
                placeholder="Shares"
                onChange={(e) => {
                    setShares(e.target.value);
                }}
                value={shares}
            />
            <input
                className="time-history"
                placeholder="Total Time"
                onChange={(e) => {
                    setTime(e.target.value);
                }}
                value={time}
            />
            <textarea
                className="note-history"
                placeholder="Enter Note...."
                onChange={(e) => {
                    setNote(e.target.value);
                }}
                value={note}
            />
            <button className="submit-history" onClick={addItem}>
                Submit
            </button>
        </div>
    );
}

export default History;
